// Mail.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Navbar from '../components/navbar';
import Sidebar from '../components/sidebar';
import InboxList from '../components/ComposeEmail'; // Tambahkan komponen InboxList
import ComposeEmail from '../components/ComposeEmail'; // Tambahkan komponen ComposeEmail
import '../styles/Mail.css';

const Mail = () => {
  const navigate = useNavigate();
  const { folder } = useParams(); // Mengambil parameter folder dari URL
  const [showInbox, setShowInbox] = useState(false);

  useEffect(() => {
    // Logika untuk menampilkan daftar inbox atau formulir pengiriman email
    setShowInbox(folder === 'inbox'); // Jika folder adalah 'inbox', tampilkan daftar inbox
  }, [folder]);

  const isAuthenticated = () => {
    const token = localStorage.getItem('token');
    return token ? true : false;
  };

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/');
    }
  }, [navigate]);

  return (
    <>
      <div className="container">
        <Navbar />
        <main className="main-content">
          {showInbox ? <InboxList /> : <ComposeEmail />}
        </main>
        <Sidebar />
      </div>
    </>
  );
};

export default Mail;
