import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/header';

const Mail = () => {
  const navigate = useNavigate();

  // Gantilah dengan cara Anda menyimpan status otentikasi
  const isAuthenticated = () => {
    // Contoh: periksa apakah token ada dan masih valid
    const token = localStorage.getItem('token'); // Gantilah dengan cara menyimpan token Anda
    return token ? true : false;
  };

  useEffect(() => {
    // Jika pengguna belum login, arahkan kembali ke halaman login
    if (!isAuthenticated()) {
      navigate('/');
    }
  }, [navigate]);

  return (
    <>
      <Header />
      {isAuthenticated() ? (
        <h2>Halaman Mail</h2>
      ) : (
        <p>Anda harus login untuk mengakses halaman ini.</p>
      )}
    </>
  );
};

export default Mail;
