import React, { useState } from 'react';
import '../SignUpForm.css'; // Import file CSS terpisah

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Perform validation
    if (!email || !password) {
      setError('Please enter both email and password.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password,
        }),
      });

      // Handle the response from the backend
      const data = await response.json();
      if (response.ok) {
        console.log('Login successful');
      } else {
        console.error('Login failed:', data.error);
        setError(data.error || 'Something went wrong');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      setError('Error logging in');
    }

    // Clear form after submission
    setEmail('');
    setPassword('');
  };

  return (
    <div className="login-container">
      <h2 style={{ color: '#fff' }}>Login - PacMail</h2>
      <form className="login-form" onSubmit={handleLoginSubmit} method="post">
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group password-input-container">
          <label htmlFor="password">Password:</label>
          <div className="password-input">
            <input
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <i
              className={`eye-icon ${showPassword ? 'fas fa-eye' : 'fas fa-eye-slash'}`}
              onClick={togglePasswordVisibility}
            ></i>
          </div>
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="submit-button">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
