// Mail.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/navbar';
import Sidebar from '../components/sidebar';
import Inbox from '../components/inbox';
import SendMailForm from '../components/sendMailForm';
import '../styles/Mail.css'; // Import file CSS baru

const Mail = () => {
  const navigate = useNavigate();
  const [selectedOption, setSelectedOption] = useState('inbox');

  return (
    <>
      <div className="container">
        <Navbar />
        <div className="mail-container">
          <Sidebar onSelectOption={(option) => setSelectedOption(option)} />
          <main>
            <h2>Mail Page</h2>
            <div className="content-container">
              {/* Render konten sesuai dengan opsi yang dipilih */}
              {selectedOption === 'inbox' && <Inbox />}
              {selectedOption === 'sendMail' && <SendMailForm />}
            </div>
          </main>
        </div>
      </div>
    </>
  );
};

export default Mail;
