import React, { useState } from 'react';
import '../SignUpForm.css'; // Import file CSS terpisah

const SignUpForm = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  };

  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Perform validation
    if (!fullName || !email || !password) {
      setError('Please enter all required information.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fullName,
          email,
          password,
        }),
      });

      // Handle the response from the backend
      const data = await response.json();
      if (response.ok) {
        console.log('Sign Up successful');
      } else {
        console.error('Sign Up failed:', data.error);
        setError(data.error || 'Something went wrong');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      setError('Error signing up');
    }

    // Clear form after submission
    setFullName('');
    setEmail('');
    setPassword('');
  };

  return (
    <div className="signup-container">
      <h2 style={{ color: '#fff' }}>Sign Up - PacMail</h2>
      <form className="signup-form" onSubmit={handleSignUpSubmit} method="post">
        <div className="form-group">
          <label htmlFor="fullName">Full Name:</label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group password-input-container">
          <label htmlFor="password">Password:</label>
          <div className="password-input">
            <input
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <i
              className={`eye-icon ${showPassword ? 'fas fa-eye' : 'fas fa-eye-slash'}`}
              onClick={togglePasswordVisibility}
            ></i>
          </div>
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="submit-button">
          Sign Up
        </button>
      </form>
    </div>
  );
};

export default SignUpForm;
