import React, { useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/header';

const Mail = () => {
  const navigate = useNavigate();

  // Gantilah dengan cara Anda menyimpan status otentikasi
  const isAuthenticated = useCallback(() => {
    // Contoh: periksa apakah token ada dan masih valid
    const token = localStorage.getItem('token'); // Gantilah dengan cara menyimpan token Anda
    return token ? true : false;
  }, []); // Dependensi kosong karena tidak ada dependensi dari luar

  useEffect(() => {
    // Cek otentikasi saat komponen pertama kali dirender
    if (!isAuthenticated()) {
      // Saat pengguna belum login, arahkan kembali ke halaman login
      console.log('Redirecting to /'); // Pastikan log ini muncul di console
      navigate('/');
    } else {
      console.log('User is authenticated.'); // Pastikan log ini muncul di console
    }
  }, [navigate, isAuthenticated]);

  // Jika tidak authenticated, berikan feedback langsung
  if (!isAuthenticated()) {
    // Gantilah dengan pesan atau elemen yang sesuai dengan desain aplikasi Anda
    return (
      <div>
        <p>Anda harus login untuk mengakses halaman ini.</p>
        <button onClick={() => navigate('/')}>Kembali ke Halaman Login</button>
      </div>
    );
  }

  return (
    <>
      <Header />
      <h2>Halaman Mail</h2>
    </>
  );
};

export default Mail;
