// Mail.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/navbar';
import Inbox from '../components/inbox'; // Import komponen Inbox
import SendMailForm from '../components/sendMailForm'; // Import komponen form pengiriman email

const Mail = () => {
  const navigate = useNavigate();
  const [selectedOption, setSelectedOption] = useState('inbox'); // Default option: 'inbox'

  // ...

  return (
    <>
      <div className="container">
        <Navbar />
        <main>
          <h2>Mail Page</h2>
          {/* Render konten sesuai dengan opsi yang dipilih */}
          {selectedOption === 'inbox' && <Inbox />}
          {selectedOption === 'sendMail' && <SendMailForm />}
        </main>
        <Sidebar onSelectOption={(option) => setSelectedOption(option)} />
      </div>
    </>
  );
};

export default Mail;
