// Sidebar.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Sidebar.css';
import { ListGroup } from 'react-bootstrap';

const Sidebar = ({ onSelectOption }) => {
  return (
    <ListGroup className="sidebar">
      <ListGroup.Item as={Link} to="/mail/inbox" onClick={() => onSelectOption('inbox')}>
        Inbox
      </ListGroup.Item>
      <ListGroup.Item as={Link} to="/mail/send" onClick={() => onSelectOption('sendMail')}>
        Send Mail
      </ListGroup.Item>
    </ListGroup>
  );
};

export default Sidebar;
