import React, { useState } from 'react';
import Navbar from '../components/navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/Sendmail.css';

const SendEmail = () => {
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [attachment, setAttachment] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubjectChange = (e) => {
    setSubject(e.target.value);
  };

  const handleBodyChange = (e) => {
    setBody(e.target.value);
  };

  const handleAttachmentChange = (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('attachment', file);

    fetch('http://localhost/upload', {
      method: 'POST',
      body: formData,
    })
    .then(response => response.json())
    .then(data => {
      setAttachment(data.attachmentLink);
    })
    .catch(error => console.error('Error uploading file:', error));
  };

  const checkUserExists = async (email) => {
    try {
      const response = await fetch('http://your-backend-url/check-user-exists', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      return data.exists;
    } catch (error) {
      console.error('Error during checkUserExists:', error);
      return false;  // Assuming user does not exist on error
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userExists = await checkUserExists(email);

      if (userExists) {
        // Rest of your code...
      } else {
        console.error('Recipient email not found in the database.');
      }
    } catch (error) {
      console.error('Error during handleSubmit:', error);
    }
  };


  return (
    <div>
      <Navbar />
      <div className="sendmail-container">
        <h2>Send Email</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-label">
            <label htmlFor="email" className="form-label">
              To:
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div className="form-label">
            <label htmlFor="subject" className="form-label">
              Subject:
            </label>
            <input
              type="text"
              className="form-control"
              id="subject"
              value={subject}
              onChange={handleSubjectChange}
              required
            />
          </div>
          <div className="form-label">
            <label htmlFor="body" className="form-label">
              Body:
            </label>
            <textarea
              className="form-control"
              id="body"
              value={body}
              onChange={handleBodyChange}
              style={{ height: '200px' }}
              required
            />
          </div>
          <div className="form-label">
            <label htmlFor="attachment" className="form-label">
              Attachment:
            </label>
            <input
              type="file"
              className="form-control"
              id="attachment"
              onChange={handleAttachmentChange}
            />
          </div>
          <button type="submit" className="btn btn-primary" style={{ fontSize: '1.1em' }}>
            Send Email
          </button>
        </form>
      </div>
    </div>
  );
};

export default SendEmail;
