// Inbox.jsx
import React, { useEffect, useState } from 'react';
import Navbar from '../components/navbar';
import DataTable from './data';

const Inbox = () => {
  const [emails, setEmailData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/get-emails', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
            'User-Email': localStorage.getItem('userEmail'),  // Pastikan email pengguna disertakan
          },
        });

        if (response.ok) {
          const data = await response.json();
          setEmailData(data);
        } else {
          console.error('Error fetching emails:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching emails:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once on mount

  return (
    <div>
      <Navbar />
      <h2>Inbox</h2>
      <DataTable data={emails} />
    </div>
  );
};

export default Inbox;
