import React, { useState } from 'react';
import Navbar from '../components/navbar';

const SendEmail = () => {
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [attachment, setAttachment] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubjectChange = (e) => {
    setSubject(e.target.value);
  };

  const handleBodyChange = (e) => {
    setBody(e.target.value);
  };

  const handleAttachmentChange = (e) => {
    // Handle file attachment
    const file = e.target.files[0];
    setAttachment(file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Implement logic to send email here
    console.log('Email sent:', { email, subject, body, attachment });

    // Reset form after submission
    setEmail('');
    setSubject('');
    setBody('');
    setAttachment(null);
  };

  return (
    <div>
      <Navbar />
      <h2>Send Email</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email Penerima:
          <input type="email" value={email} onChange={handleEmailChange} required />
        </label>
        <br />
        <label>
          Subject:
          <input type="text" value={subject} onChange={handleSubjectChange} required />
        </label>
        <br />
        <label>
          Body:
          <textarea value={body} onChange={handleBodyChange} required />
        </label>
        <br />
        <label>
          Attachment:
          <input type="file" onChange={handleAttachmentChange} />
        </label>
        <br />
        <button type="submit">Kirim Email</button>
      </form>
    </div>
  );
};

export default SendEmail;
