import React, { useState } from 'react';
import Navbar from '../components/navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/Sendmail.css';

const SendEmail = () => {
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [attachment, setAttachment] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubjectChange = (e) => {
    setSubject(e.target.value);
  };

  const handleBodyChange = (e) => {
    setBody(e.target.value);
  };

  const handleAttachmentChange = (e) => {
    const file = e.target.files[0];
    setAttachment(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Convert attachment to ArrayBuffer
    const attachmentBuffer = await attachment.arrayBuffer();
    const attachmentBlob = new Blob([attachmentBuffer], { type: attachment.type });

    // Send data to the backend
    const formData = new FormData();
    formData.append('recepient_email', email);
    formData.append('recepient_data', JSON.stringify({ subject, body }));
    formData.append('attachment', attachmentBlob, attachment.name);

    try {
      const response = await fetch('http://localhost:5000/send_email', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        console.log('Email sent successfully');
        setEmail('');
        setSubject('');
        setBody('');
        setAttachment(null);
      } else {
        console.error('Error sending email:', result.error);
      }
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="sendmail-container">
        <h2>Send Email</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-label">
            <label htmlFor="email" className="form-label">
              To :
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div className="form-label">
            <label htmlFor="subject" className="form-label">
              Subject:
            </label>
            <input
              type="text"
              className="form-control"
              id="subject"
              value={subject}
              onChange={handleSubjectChange}
              required
            />
          </div>
          <div className="form-label">
            <label htmlFor="body" className="form-label">
              Body:
            </label>
            <textarea
              className="form-control"
              id="body"
              value={body}
              onChange={handleBodyChange}
              style={{ height: '200px' }}
              required
            />
          </div>
          <div className="form-label">
            <label htmlFor="attachment" className="form-label">
              Attachment:
            </label>
            <input
              type="file"
              className="form-control"
              id="attachment"
              onChange={handleAttachmentChange}
            />
          </div>
          <button type="submit" className="btn btn-primary" style={{ fontSize: '1.1em' }}>
            Send Email
          </button>
        </form>
      </div>
    </div>
  );
};

export default SendEmail;
